import React, { Component } from 'react';
import queryString from 'query-string';
class GetShowRoomInfo extends Component {
  state = {
    todos: []
  };

  async componentDidMount() {
    const values = queryString.parse(this.props.location.search)
    try {
      const res = await fetch("http://127.0.0.1:8000/ShowRoom/" + this.props.match.params.id,);
      const todos = await res.json();
      this.setState({
        todos
      });
    } catch (e) {
      console.log(e);
    }
  }

  render(){
    return (
      <div className='container'>
        <div className="row">
              {this.state.todos.map(item => (
                <div className="card">
                  <div className="card-body">
                     <div key={item.id}>
                       <h4 className="text-center">EmployeeInfo</h4>
                       <label><b>Employee Id:</b></label>
                       <span >{item.id}</span>
                       <br/>
                       <label><b>Name:</b></label>
                       <span >{item.name}</span>
                       <br/>
                       <label><b>RegistrationNumber:</b></label>
                       <span className="text-center">{item.RegistrationNumber}</span>
                       <br/>
                       <label><b>logoType:</b></label>
                       <span>{item.logoType}</span>
                       <br/>
                     </div>
                  </div> 
               </div>
              ))}
        </div>
      </div>
    );
  }
}

export default GetShowRoomInfo ;
