import React, { Component } from 'react';
import axios from 'axios';

var panelStyle = {
	'maxWidth': '80%',
	margin: '0 auto'
}
class CarAssignShowroom extends Component {
  constructor(props) {
    super(props);
    this.state={
      car:[],
      showroom:[],
  }
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    var self = this;
    fetch('http://localhost:8000/allcardetail/')
    .then(function (res){
        return res.json();
    })
    .then(function(data){
        console.log(data);
        self.setState({
            car:data,
        })
    })

    fetch('http://localhost:8000/showroom/')
    .then(function (res){
        return res.json();
    })
    .then(function(data){
        console.log(data);
        self.setState({
          showroom:data,
        })
    })
  }

  handleSubmit(event) {
    event.preventDefault();
    const data = new FormData(event.target);
    fetch('http://localhost:8000/carassigntoshowroom/', {
      method: 'POST',
      body: data,
    });  
  }

  handleSelectValue() {
  if (this.refs.showroom) {
    return(this.refs.showroom.value);
    }
  }

  render() {
    return(
          <div className="container">
            <div className="row">
                <div className="col-md-8 offset-md-2">
                    <form onSubmit={this.handleSubmit} method="post">
                        <h4 className="text-center">Car Assign Showroom</h4>
                        <div className="row">
                            <div className="col-md-8 offset-md-2">
                                <label>Choose Car:</label>
                                <select className='form-control' name="car">
                                {this.state.car.map(item => (
                                    <option value={item.id}>{item.manufacture}({item.id})</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-8 offset-md-2">
                                <label>Choose ShowRoom:</label>
                                <select className='form-control' name="showroom">
                                {this.state.showroom.map(item => (
                                    <option value={item.id}>{item.name}({item.id})</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-8 offset-md-2">
                                <input type="submit" className="btn btn-info btn-block shadow-none" value="Assign"/>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
          </div>
    );
  }

  inputChangeHandler(e) {
   let formFields = {...this.state.formFields};
   formFields[e.target.name] = e.target.value;
   this.setState({
    formFields
   });
  }

  formHandler(formFields) {
   axios.post('/api/register', formFields)
     .then(function(response){
       console.log(response);
       //Perform action based on response
   })
     .catch(function(error){
       console.log(error);
       //Perform action based on error
     });
  }
}

export default CarAssignShowroom
