import React, { Component } from 'react';
import queryString from 'query-string';
class GetShowRoomInfo extends Component {
    constructor(props){
        super(props);
        this.state={
            person:{},
        }
}

  componentDidMount(){
    var self = this;
    const queryParams = new URLSearchParams(this.props.location.search);
    var id = queryParams.get('id');
    fetch('http://127.0.0.1:8000/ShowRoom/'+id)
    .then(function (res){
        return res.json();
    })
    .then(function(data){
        console.log(data);
        self.setState({
            person:data,
        })
})
  }

  render(){
    return (
      <div className='container'>
        <div className="row">
                <div className="card">
                  <div className="card-body">
                     <div key={this.state.person.id}>
                       <h4 className="text-center">EmployeeInfo</h4>
                       <label><b>Employee Id:</b></label>
                       <span >{this.state.person.id}</span>
                       <br/>
                       <label><b>Name:</b></label>
                       <span >{this.state.person.name}</span>
                       <br/>
                       <label><b>RegistrationNumber:</b></label>
                       <span className="text-center">{this.state.person.RegistrationNumber}</span>
                       <br/>
                       <label><b>logoType:</b></label>
                       <span>{this.state.person.logoType}</span>
                       <br/>
                     </div>
                  </div> 
               </div>
        </div>
      </div>
    );
  }
}

export default GetShowRoomInfo ;
